document.addEventListener("DOMContentLoaded",(function(){chrome.storage.sync.get({emotesUrl:"",accessKey:"",defaultEmoteSize:"2em",largeEmoteSize:"4em"},(function(e){document.getElementById("emotes-url").value=e.emotesUrl,document.getElementById("access-key").value=e.accessKey,document.getElementById("default-emote-size").value=e.defaultEmoteSize,document.getElementById("large-emote-size").value=e.largeEmoteSize}))})),document.getElementById("save").addEventListener("click",(function(){var e=document.getElementById("emotes-url").value,t=document.getElementById("access-key").value,n=document.getElementById("default-emote-size").value,m=document.getElementById("large-emote-size").value;chrome.storage.sync.set({emotesUrl:e,accessKey:t,defaultEmoteSize:n,largeEmoteSize:m},(function(){var e=document.getElementById("status");e.textContent="Options saved.",setTimeout((function(){e.textContent=""}),750)}))}));