document.addEventListener("DOMContentLoaded",(function(){chrome.storage.sync.get({emotesUrl:"",accessKey:"",defaultEmoteSize:"2em",largeEmoteSize:"4em",useFrozenEmotes:!1,emoteSuggestionAmount:10},(function(e){document.getElementById("emotes-url").value=e.emotesUrl,document.getElementById("access-key").value=e.accessKey,document.getElementById("default-emote-size").value=e.defaultEmoteSize,document.getElementById("large-emote-size").value=e.largeEmoteSize,document.getElementById("use-frozen-emotes").checked=e.useFrozenEmotes,document.getElementById("emote-suggestion-amount").value=e.emoteSuggestionAmount}))})),document.getElementById("save").addEventListener("click",(function(){var e=document.getElementById("emotes-url").value,t=document.getElementById("access-key").value,o=document.getElementById("default-emote-size").value,n=document.getElementById("large-emote-size").value,m=document.getElementById("use-frozen-emotes").checked,u=document.getElementById("emote-suggestion-amount").value;chrome.storage.sync.set({emotesUrl:e,accessKey:t,defaultEmoteSize:o,largeEmoteSize:n,useFrozenEmotes:m,emoteSuggestionAmount:u},(function(){var e=document.getElementById("status");e.textContent="Options saved.",setTimeout((function(){e.textContent=""}),750)}))}));